mapper.delete:

delete from word_recited
where USERNAME = #{username,jdbcType=VARCHAR}
  and WORD_NAME = #{wordName,jdbcType=VARCHAR}

mapper.update:

update word_recited
set REPEATS = #{repeats,jdbcType=INTEGER},
  LAST_RECITE = #{lastRecite,jdbcType=TIMESTAMP}
where USERNAME = #{username,jdbcType=VARCHAR}
  and WORD_NAME = #{wordName,jdbcType=VARCHAR}

mapper.select:

select USERNAME, WORD_NAME, REPEATS, LAST_RECITE
from word_recited
where USERNAME = #{username,jdbcType=VARCHAR}
  and WORD_NAME = #{wordName,jdbcType=VARCHAR}

select count(*) from word_recited
where USERNAME = #{username,jdbcType=VARCHAR}
  and WORD_NAME = #{wordName,jdbcType=VARCHAR}
